public class output_program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0,j=0;
		for(i=9;i>=1;i--)
		{
			for(j=0;j<i;j++)
			{
				System.out.print(i);
			}
			System.out.print("\n");
		}

	}

}
